import React from "react";
import CategoryFilter from "../components/CategoryFilter";
import BrandFilter from "../components/BrandFilter";
import ProductList from "../components/ProductList";
import { Container, Row, Col, Button } from "react-bootstrap"; // Import React-Bootstrap
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import Search from "../components/Search";

const HomePage = () => {
    return (
        <Container>
            <Row className="my-4">
                <Col md={3}>
                    <div className="p-3">
                        <CategoryFilter />
                        <hr />
                        <BrandFilter />
                        <hr />
                        <Search /> {/* Add Search component here */}
                        <hr />
                        <Link to="/add-product">
                            <Button variant="primary" block>Add Product</Button>
                        </Link>
                    </div>
                </Col>
                <Col md={9}>
                    <h3 className="text-center mb-4">List of Products</h3>
                    <ProductList />
                </Col>
            </Row>
        </Container>
    );
};

export default HomePage;
