// src/components/Search.js
import React, { useContext } from 'react';
import { Form } from 'react-bootstrap';
import { ProductContext } from '../context/ProductContext';

const Search = () => {
  const { searchTerm, setSearchTerm, fullTimeFilter, setFullTimeFilter } = useContext(ProductContext);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleFullTimeChange = (e) => {
    const { name, checked } = e.target;
    setFullTimeFilter({ ...fullTimeFilter, [name]: checked });
  };

  return (
    <Form>
      <Form.Group>
        <Form.Label>Tìm kiếm</Form.Label>
        <Form.Control
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </Form.Group>
      <Form.Group>
        <Form.Check
          type="checkbox"
          label="Full-time"
          name="fullTime"
          checked={fullTimeFilter.fullTime}
          onChange={handleFullTimeChange}
        />
        <Form.Check
          type="checkbox"
          label="Part-time"
          name="partTime"
          checked={fullTimeFilter.partTime}
          onChange={handleFullTimeChange}
        />
      </Form.Group>
    </Form>
  );
};

export default Search;
