import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { Button, Image, Badge } from "react-bootstrap";

const ProductDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            const res = await axios.get(`http://localhost:9999/product/${id}`);
            setProduct(res.data);
        };
        fetchProduct();
    }, [id]);

    const handleDelete = async () => {
        const confirmDelete = window.confirm("Do you want to delete this product?");
        if (confirmDelete) {
            await axios.delete(`http://localhost:9999/product/${id}`);
            navigate("/");
        }
    };

    if (!product) return <div>Loading...</div>;

    return (
        <div className="text-center">
            <Image
                src={`http://localhost:9999/images/${product.image}`}
                alt={product.title}
                className="mb-4"
                fluid
            />
            <h3>{product.title}</h3>
            <p className="text-muted">{product.description}</p>
            <h4>
                Price: <Badge bg="success">${product.price}</Badge>
            </h4>
            <h5>
                Discount: <Badge bg="info">{product.discountPercentage}%</Badge>
            </h5>
            <h5>
                New Price:{" "}
                <Badge bg="warning">
                    $
                    {(
                        product.price -
                        (product.price * product.discountPercentage) / 100
                    ).toFixed(2)}
                </Badge>
            </h5>
            <p>Rating: {product.rating}</p>
            <p>Stock: {product.stock}</p>
            <div className="mt-4">
                <Button variant="secondary" className="me-3" onClick={() => navigate("/")}>
                    Back to List
                </Button>
                <Button variant="danger" onClick={handleDelete}>
                    Delete
                </Button>
            </div>
        </div>
    );
};

export default ProductDetail;
