import React, { useContext } from "react";
import { ProductContext } from "../context/ProductContext";

const BrandFilter = () => {
    const { brands, setFilters } = useContext(ProductContext);

    return (
        <div>
            <h5>Brands</h5>
            {brands.map((brand) => (
                <div key={brand.id}>
                    <input
                        type="radio"
                        name="brand"
                        value={brand.id}
                        onChange={(e) =>
                            setFilters((prev) => ({ ...prev, brand: e.target.value }))
                        }
                    />
                    {brand.name}
                </div>
            ))}
        </div>
    );
};

export default BrandFilter;
